import React, { useContext, useEffect, useState } from 'react';
import { GlobalState } from '../../../GlobalState';
import ProductItem from '../utils/ProductItem/ProductItem';
import axios from 'axios';
import './Products.css'

const Products = () => {
  const state = useContext(GlobalState)
  // const [products] = state.productsAPI.products
  // console.log(products)


  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState(products);

  useEffect(() => {
    const getPoducts = async () => {

      const response = await axios.get("/api/products")
      const newData = response.data.products
      setProducts(newData);
      setFilter(newData);
      console.log(newData);

    }
    getPoducts();
  }, [])

  const filterProducts = (cat) => {
    const newProductCat = products.filter((x) => x.category === cat)
    setFilter(newProductCat)
  }
  const filterPrice = (price) => {
    const newProductPrice = products.filter((x) => x.price <= price)
    setFilter(newProductPrice)
  }


  return (
    <section>
      <div className='container'>
        <ul className='filters'>
          <li className='filter-btn ' onClick={() => setFilter(products)}>All</li>
          <li className='filter-btn ' onClick={() => filterProducts("nike")}>Nike</li>
          <li className='filter-btn ' >Jordan</li>
          <li className='filter-btn ' >Jordan</li>
          {/* <label for="cars">Choose a car:</label>

          <select name="cars" id="cars">
            <option value="volvo">Volvo</option>
            <option value="saab">Saab</option>
            <option value="mercedes">Mercedes</option>
            <option value="audi">Audi</option>
          </select> */}
        </ul>
        <div className="filter-price">
          <label for="cars">Choose a car:</label>

          <select name="cars" id="cars">
            <option value="volvo">Volvo</option>
            <option value="saab">Saab</option>
            <option value="mercedes">Mercedes</option>
            <option value="audi">Audi</option>
          </select>
        </div>
        <div className='products'>
          {
            filter.map(product => {
              return <ProductItem product={product} key={product._id} />
            })
          }
        </div>
      </div>
    </section>
  )
}

export default Products
