import React , {useContext} from 'react';
import {GlobalState} from '../../../GlobalState'

const Cart = () => {
  const state = useContext(GlobalState)
  const [cart] = state.userAPI.cart

  if(cart.length === 0){
    return <h1 style={{textAlign: "center" , fontSize: "30px"}}> Cart is empty</h1>
  }
  return (
    <div>Cart</div>
  )
}

export default Cart
