import React, {useState} from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  const [user, setUser] = useState({
    email: '', password: ''
  })

  const onChangeInput = e => {
    const {name , value } = e.target;
    setUser({...user , [name]: value})
  }

  return (
    <div className='loginContainer'>
      <form action="" className="login">
        <input className="login-email" type="email" name="email" required placeholder="Email..." value={user.email} onChange={onChangeInput}/>
        <input className="login-password" type="password" name="password" placeholder="Password" value={user.password} onChange={onChangeInput}/>
        <div className="login-btn">
          <button className="login-submit" type='submit'>Login</button>
          <Link to='/register'>Register</Link>
        </div>
      </form>
    </div>
  )
}

export default Login
