import React, { useContext } from 'react';
import { FaUserAlt } from 'react-icons/fa';
import { FiShoppingCart } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import { GlobalState } from '../../GlobalState';
import './styleHeader.css';

//vd 

const Header = () => {
    const state = useContext(GlobalState)
    const [isLogged, setIsLogged] = state.userAPI.isLogged
    const [isAdmin, setIsAdmin] = state.userAPI.isAdmin

    const adminRouter = () => {
      return (
        <>
          <li><Link to="/create_product" style={{color: "white"}}> Create product</Link></li>
          <li><Link to="/category"> Categories</Link></li>
        </>
      )
    }
    const loggedRouter = () => {
      return (
        <>
          <li><Link to="/history" style={{color: "white"}}> History</Link></li>
          <li><Link to="/"> Logout</Link></li>
        </>
      )
    }


  return (
    <nav className='nav-bar'>
      <div className='logo'><Link to="/">{isAdmin ? 'Admin' : 'Logo'}</Link></div>
      <ul className='nav-bar__list'>
        <li className='list-item'><Link to="/products">{ isAdmin ? 'Product' : 'Shop'}</Link></li>
        <li className='list-item'><Link to="/about">About</Link></li>
        <li className='list-item'><Link to="/contact">Contact</Link></li>
      </ul>
      <ul className='icons'>
          <li className='list-item'>
            {
              isLogged ? loggedRouter : <Link to="/login" >
              <FaUserAlt size={24}/>
            </Link>
            }
            
          </li >
          <li className='list-item' style={{display: 'flex'}}>
            <Link to="/cart"><FiShoppingCart size={24}/></Link>
            <span style={{color: "white" , paddingLeft: "10px"}}>(0)</span>
            </li>
      </ul> 
    </nav>
  )
}

export default Header
