import React, { useContext  , useEffect, useState} from 'react';
import { GlobalState } from '../../../GlobalState';
import ProductItem from '../utils/ProductItem/ProductItem';
import axios from 'axios';
import './Products.css'

const Products = () => {
  const state = useContext(GlobalState)
  // const [products] = state.productsAPI.products
  // console.log(products)


  const [products, setProducts] = useState(state.productsAPI.products);
  const [filter, setFilter] = useState(products);

    // useEffect(() => {
    //     const getPoducts = async () => {

    //         const response = await axios.get("/api/products")
    //         const newData = response.data.products
    //           setProducts(newData);
    //           setFilter(newData);
    //           console.log(newData);

    //     }
    //     getPoducts();
    // }, [])

    const filterProducts = (cat) => {
      const newProduct = products.filter((x) => x.category === cat)
      setFilter(newProduct)
    }


  return (
    <section>
      <div className='container'>
        <div className='filters'>
          <button className='filter-btn ' onClick={() => setFilter(products)}>All</button>
          <button className='filter-btn ' onClick={() => filterProducts("giay nu")}>giay nu</button>
          <button className='filter-btn ' onClick={() => filterProducts("nike")}>Nike</button>
          <button  className='filter-btn ' >Jordan</button>
        </div>
        <div className='products'>
          {
            filter.map(product => {
              return <ProductItem product={product} key={product._id} />
            })
          }
        </div>
      </div>
    </section>
  )
}

export default Products
