import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [user, setUser] = useState({
    email: '', password: ''
  })

  const onChangeInput = e => {
    const {name , value } = e.target;
    setUser({...user , [name]: value})
  }

  const loginSubmit = async (e) => {
    e.prevenDefault()
    try {
      await axios.post('/user/login' , {...user})

      localStorage.setItem('first login' , true)

      window.location.href = "/"
    } catch (err) {
        alert(err.response.data.msg)
    }
  }

  return (
    <div className='loginContainer'>
      <form action="" className="login">
        <input className="login-email" type="email" name="email" required placeholder="Email..." value={user.email} onChange={onChangeInput}/>

        <input className="login-password" type="password" name="password" required autoComplete='on' placeholder="Password" value={user.password} onChange={onChangeInput}/>

        <div className="login-btn">
          <button className="login-submit" type='submit'>Login</button>
          <Link to='/register'>Register</Link>
        </div>
      </form>
    </div>
  )
}

export default Login
