import React, { useContext  , useEffect, useState} from 'react';
import { GlobalState } from '../../../GlobalState';
import ProductItem from '../utils/ProductItem/ProductItem';
import axios from 'axios';
import './Products.css'

const Products = () => {
  // const state = useContext(GlobalState)
  // const [products] = state.productsAPI.products
  // console.log(products)


  const [products , setProduct] = useState([])
  const [filter , setFilter] = useState(products)

  useEffect(() => {
    const fetchapi = async() => {
        const res = await axios.get('/api/products')
        setProduct(res.data.products)
    }
    fetchapi()
  },[])
  console.log(products)


  const filterProducts = (cat) => {
    const newProducts = products.filter((x) => x.category === cat)
    setProduct(newProducts)
  }



  return (
    <section>
      <div className='container'>
        <div className='filters'>
          <button className='filter-btn ' onClick={() => setProduct(products)}>All</button>
          <button className='filter-btn ' onClick={() => filterProducts("giay nu")}>giay nu</button>
          <button className='filter-btn ' onClick={() => filterProducts("nike")}>Nike</button>
          <button  className='filter-btn ' >Jordan</button>
        </div>
        <div className='products'>
          {
            filter.map(product => {
              return <ProductItem product={product} key={product._id} />
            })
          }
        </div>
      </div>
    </section>
  )
}

export default Products
